mapper.update:
UPDATE TBL_BOARD
   SET TITLE = #{title, jdbcType=VARCHAR}
     , CONTENT = #{content, jdbcType=VARCHAR}
     , MOD_ID = #{cust_id, jdbcType=VARCHAR}
     , MOD_DATE = SYSDATE
 WHERE BSEQ = #{bseq, jdbcType=VARCHAR}

mapper.insert:
	  INSERT INTO BBS_COMMENT(
